import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom'; // Importar useParams y useNavigate
import { getProducto, getImage } from '../services/productoService';

const ProductoDetail = () => {
  const { id } = useParams();
  const navigate = useNavigate(); // Hook para la navegación
  const [producto, setProducto] = useState(null);

  useEffect(() => {
    fetchProducto();
  }, []);

  const fetchProducto = async () => {
    const response = await getProducto(id);
    setProducto(response.data.producto);
  };

  if (!producto) return <div>Cargando...</div>;

  const handleBackClick = () => {
    navigate('/'); // Navegar al inicio
  };

  const containerStyle = {
    maxWidth: '600px',
    margin: '20px auto',
    padding: '20px',
    border: '1px solid #ddd',
    borderRadius: '8px',
    boxShadow: '0 0 10px rgba(0, 0, 0, 0.1)',
    textAlign: 'center'
  };

  const imageStyle = {
    maxWidth: '100%',
    height: 'auto',
    marginTop: '20px'
  };

  const buttonStyle = {
    backgroundColor: '#007bff',
    color: '#fff',
    border: 'none',
    borderRadius: '4px',
    padding: '10px 20px',
    cursor: 'pointer',
    marginTop: '20px'
  };

  return (
    <div style={containerStyle}>
      <h2>{producto.nombre}</h2>
      <p>{producto.descripcion}</p>
      <p>{producto.edicion}</p>
      <p>{producto.anio}</p>
      <p>{producto.precio}</p>
      {producto.imagen && <img src={getImage(producto.imagen)} alt={producto.nombre} style={imageStyle} />}
      <button style={buttonStyle} onClick={handleBackClick}>Volver al Inicio</button>
    </div>
  );
};

export default ProductoDetail;
