import React, { useEffect, useState } from 'react';
import { getProductos, deleteProducto } from '../services/productoService';
import ProductoForm from './ProductoForm';

const ProductoList = () => {
  const [productos, setProductos] = useState([]);

  useEffect(() => {
    fetchProductos();
  }, []);

  const fetchProductos = async () => {
    const response = await getProductos();
    setProductos(response.data.productos);
  };

  const handleDelete = async (id) => {
    await deleteProducto(id);
    fetchProductos();
  };

  const containerStyle = {
    maxWidth: '600px',
    margin: '20px auto',
    padding: '20px',
    border: '1px solid #ddd',
    borderRadius: '8px',
    boxShadow: '0 0 10px rgba(0, 0, 0, 0.1)'
  };

  const titleStyle = {
    fontSize: '24px',
    fontWeight: 'bold',
    marginBottom: '20px',
    textAlign: 'center'
  };

  const listItemStyle = {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    padding: '10px',
    borderBottom: '1px solid #ddd'
  };

  const buttonStyle = {
    backgroundColor: '#ff4d4d',
    color: '#fff',
    border: 'none',
    borderRadius: '4px',
    padding: '5px 10px',
    cursor: 'pointer',
  };

  const formStyle = {
    marginBottom: '20px'
  };

  return (
    <div style={containerStyle}>
      <h2 style={titleStyle}>Lista de Productos</h2>
      <div style={formStyle}>
        <ProductoForm fetchProductos={fetchProductos} />
      </div>
      <br />
      <ul>
        {productos.map(producto => (
          console.log(producto),
          <li key={producto._id} style={listItemStyle} onClick={goToProducto}>
            {producto.nombre}
            {producto.descripcion}
            <button style={buttonStyle} onClick={() => handleDelete(producto._id)}>Eliminar</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default ProductoList;
