import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom'; // Importar useParams y useNavigate
import { getProducto, updateProducto } from '../services/productoService';

const ProductoEdit = () => {
  const { id } = useParams();
  const navigate = useNavigate(); // Hook para la navegación
  const [formData, setFormData] = useState({
    nombre: '',
    descripcion: '',
    edicion: '',
    anio: '',
    precio: '',
    imagen: null
  });

  useEffect(() => {
    fetchProducto();
  }, []);

  const fetchProducto = async () => {
    const response = await getProducto(id);
    setFormData(response.data.producto);
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevData => ({
      ...prevData,
      [name]: value
    }));
  };

  const handleImageChange = (e) => {
    setFormData(prevData => ({
      ...prevData,
      imagen: e.target.files[0] // Guarda el archivo de imagen
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const updatedData = new FormData();
    updatedData.append('nombre', formData.nombre);
    updatedData.append('descripcion', formData.descripcion);
    updatedData.append('edicion', formData.edicion);
    updatedData.append('anio', formData.anio);
    updatedData.append('precio', formData.precio);
    if (formData.imagen) {
      updatedData.append('imagen', formData.imagen);
    }

    await updateProducto(id, updatedData);
    navigate(`/producto/${id}`); // Navegar al detalle del producto después de guardar
  };

  const handleBackClick = () => {
    navigate(`/producto/${id}`); // Navegar al detalle del producto
  };

  const containerStyle = {
    maxWidth: '600px',
    margin: '20px auto',
    padding: '20px',
    border: '1px solid #ddd',
    borderRadius: '8px',
    boxShadow: '0 0 10px rgba(0, 0, 0, 0.1)'
  };

  const titleStyle = {
    fontSize: '24px',
    fontWeight: 'bold',
    marginBottom: '20px',
    textAlign: 'center'
  };

  const formStyle = {
    display: 'flex',
    flexDirection: 'column',
    gap: '15px'
  };

  const inputStyle = {
    padding: '10px',
    border: '1px solid #ddd',
    borderRadius: '4px'
  };

  const buttonStyle = {
    backgroundColor: '#007bff',
    color: '#fff',
    border: 'none',
    borderRadius: '4px',
    padding: '10px 20px',
    cursor: 'pointer',
    marginTop: '10px'
  };

  return (
    <div style={containerStyle}>
      <h2 style={titleStyle}>Editar Producto</h2>
      <form style={formStyle} onSubmit={handleSubmit}>
        <input
          type="text"
          name="nombre"
          value={formData.nombre}
          onChange={handleChange}
          placeholder="Nombre"
          style={inputStyle}
        />
        <input
          type="text"
          name="descripcion"
          value={formData.descripcion}
          onChange={handleChange}
          placeholder="Descripción"
          style={inputStyle}
        />
        <input
          type="text"
          name="edicion"
          value={formData.edicion}
          onChange={handleChange}
          placeholder="Edición"
          style={inputStyle}
        />
        <input
          type="number"
          name="anio"
          value={formData.anio}
          onChange={handleChange}
          placeholder="Año"
          style={inputStyle}
        />
        <input
          type="number"
          name="precio"
          value={formData.precio}
          onChange={handleChange}
          placeholder="Precio"
          style={inputStyle}
        />
        <input
          type="file"
          name="imagen"
          onChange={handleImageChange}
        />
        <button type="submit" style={buttonStyle}>Guardar Cambios</button>
        <button type="button" style={buttonStyle} onClick={handleBackClick}>Volver al Detalle</button>
      </form>
    </div>
  );
};

export default ProductoEdit;
