import React, { useState } from 'react';
import { saveProducto, updateProducto, uploadImage } from '../services/productoService';

const ProductoForm = ({ producto, isEdit, fetchProductos }) => {
  const [formData, setFormData] = useState({
    nombre: producto?.nombre || '',
    descripcion: producto?.descripcion || '',
    edicion: producto?.edicion || '',
    anio: producto?.anio || '',
    precio: producto?.precio || '',
    imagen: producto?.imagen || null
  });

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleImageChange = (e) => {
    setFormData({ ...formData, imagen: e.target.files[0] });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (isEdit) {
      await updateProducto(producto._id, formData);
    } else {
      const response = await saveProducto(formData);
      if (formData.imagen) {
        const imageFormData = new FormData();
        imageFormData.append('imagen', formData.imagen);
        await uploadImage(response.data.libro._id, imageFormData);
      }
    }
    fetchProductos();
  };

  const formStyle = {
    display: 'flex',
    flexDirection: 'column',
    width: '300px',
    margin: 'auto'
  };

  const inputStyle = {
    marginBottom: '10px',
    padding: '8px',
    border: '1px solid #ccc',
    borderRadius: '4px'
  };

  const buttonStyle = {
    padding: '10px',
    backgroundColor: '#28a745',
    color: 'white',
    border: 'none',
    borderRadius: '4px',
    cursor: 'pointer'
  };

  const buttonHoverStyle = {
    backgroundColor: '#218838'
  };

  return (
    <form onSubmit={handleSubmit} style={formStyle}>
      <input 
        type="text" 
        name="nombre" 
        value={formData.nombre} 
        onChange={handleChange} 
        placeholder="Nombre" 
        style={inputStyle} 
      />
      <input 
        type="text" 
        name="descripcion" 
        value={formData.descripcion} 
        onChange={handleChange} 
        placeholder="Descripción" 
        style={inputStyle} 
      />
      <input 
        type="text" 
        name="edicion" 
        value={formData.edicion} 
        onChange={handleChange} 
        placeholder="Edición" 
        style={inputStyle} 
      />
      <input 
        type="number" 
        name="anio" 
        value={formData.anio} 
        onChange={handleChange} 
        placeholder="Año" 
        style={inputStyle} 
      />
      <input 
        type="number" 
        name="precio" 
        value={formData.precio} 
        onChange={handleChange} 
        placeholder="Precio" 
        style={inputStyle} 
      />
      <input 
        type="file" 
        name="imagen" 
        onChange={handleImageChange} 
        style={inputStyle} 
      />
      <button 
        type="submit" 
        style={{ ...buttonStyle, ':hover': buttonHoverStyle }}
      >
        Guardar
      </button>
    </form>
  );
};

export default ProductoForm;
