import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom'; // Importar useParams
import { getProducto, getImage } from '../services/productoService';
import "../styles/ProductoDetail.css"; // Importar estilos

const ProductoDetail = () => {
  const { id } = useParams(); // Obtener el parámetro id
  const [producto, setProducto] = useState(null);

  useEffect(() => {
    fetchProducto();
  }, []);

  const fetchProducto = async () => {
    const response = await getProducto(id);
    setProducto(response.data.producto);
  };

  if (!producto) return <div>Cargando...</div>;

  return (
    <div className="producto-detail-container">
      <h2>{producto.nombre}</h2>
      <p>{producto.descripcion}</p>
      <p>{producto.edicion}</p>
      <p>{producto.anio}</p>
      <p>{producto.precio}</p>
      {producto.imagen && <img src={getImage(producto.imagen)} alt={producto.nombre} />}
    </div>
  );
};

export default ProductoDetail;
