import React, { useEffect, useState } from 'react';
import { getProductos, deleteProducto } from '../services/productoService';
import ProductoForm from './ProductoForm';

const ProductoList = () => {
  const [productos, setProductos] = useState([]);
  const [hoveredId, setHoveredId] = useState(null);

  useEffect(() => {
    fetchProductos();
  }, []);

  const fetchProductos = async () => {
    const response = await getProductos();
    setProductos(response.data.productos);
  };

  const handleDelete = async (id) => {
    await deleteProducto(id);
    fetchProductos();
  };

  const goToProduct = (producto) => {
    window.location.href = "/producto/" + producto;
  };

  const containerStyle = {
    maxWidth: '600px',
    margin: '20px auto',
    padding: '20px',
    border: '1px solid #ddd',
    borderRadius: '8px',
    boxShadow: '0 0 10px rgba(0, 0, 0, 0.1)'
  };

  const titleStyle = {
    fontSize: '24px',
    fontWeight: 'bold',
    marginBottom: '20px',
    textAlign: 'center'
  };

  const listItemStyle = {
    padding: '10px',
    borderBottom: '1px solid #ddd',
    cursor: 'pointer',
    display: 'flex',
    flexDirection: 'column', // Cambiado para apilar los elementos verticalmente
  };

  const nameStyle = (isHovered) => ({
    fontWeight: 'bold',
    marginBottom: '5px',
    transition: 'color 0.3s ease', // Transición para el efecto de hover
    color: isHovered ? '#007bff' : '#000' // Color del nombre cuando el mouse está sobre el elemento
  });

  const descriptionStyle = {
    marginBottom: '10px',
    color: '#555'
  };

  const buttonStyle = {
    backgroundColor: '#ff4d4d',
    color: '#fff',
    border: 'none',
    borderRadius: '4px',
    padding: '5px 10px',
    cursor: 'pointer',
  };

  const formStyle = {
    marginBottom: '20px'
  };

  return (
    <div style={containerStyle}>
      <h2 style={titleStyle}>Lista de Productos</h2>
      <div style={formStyle}>
        <ProductoForm fetchProductos={fetchProductos} />
      </div>
      <ul>
        {productos.map(producto => (
          <li
            key={producto._id}
            style={listItemStyle}
            onMouseEnter={() => setHoveredId(producto._id)}
            onMouseLeave={() => setHoveredId(null)}
            onClick={() => goToProduct(producto._id)}
          >
            <span
              style={nameStyle(hoveredId === producto._id)}
            >
              {producto.nombre}
            </span>
            <span style={descriptionStyle}>{producto.descripcion}</span>
            <button
              style={buttonStyle}
              onClick={(e) => {
                e.stopPropagation(); // Evitar que el click en el botón también active el onClick del li
                handleDelete(producto._id);
              }}
            >
              Eliminar
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default ProductoList;
