import React, { useEffect, useState } from 'react';
import { getProducto, getImage } from '../services/productoService';

const ProductoDetail = ({ match }) => {
  const [producto, setProducto] = useState(null);

  useEffect(() => {
    fetchProducto();
  }, []);

  const fetchProducto = async () => {
    const response = await getProducto(match.params.id);
    setProducto(response.data.producto);
  };

  if (!producto) return <div>Cargando...</div>;

  const containerStyle = {
    maxWidth: '600px',
    margin: '20px auto',
    padding: '20px',
    border: '1px solid #ddd',
    borderRadius: '8px',
    boxShadow: '0 0 10px rgba(0, 0, 0, 0.1)'
  };

  const titleStyle = {
    fontSize: '24px',
    fontWeight: 'bold',
    marginBottom: '10px'
  };

  const textStyle = {
    fontSize: '18px',
    marginBottom: '10px'
  };

  const imageStyle = {
    maxWidth: '100%',
    height: 'auto',
    borderRadius: '8px',
    marginTop: '10px'
  };

  return (
    <div style={containerStyle}>
      <h2 style={titleStyle}>{producto.nombre}</h2>
      <p style={textStyle}>{producto.descripcion}</p>
      <p style={textStyle}>Edición: {producto.edicion}</p>
      <p style={textStyle}>Año: {producto.anio}</p>
      <p style={textStyle}>Precio: {producto.precio}</p>
      {producto.imagen && (
        <img 
          src={getImage(producto.imagen)} 
          alt={producto.nombre} 
          style={imageStyle}
        />
      )}
    </div>
  );
};

export default ProductoDetail;
